function (doc, meta) {
  if (doc.fid) {
   emit(doc.fid, {
   "floor": doc.floor,
   "building": doc.buid,
   "deviceId": doc.dvid,
   "gps_lat": doc.gps_lat,
   "gps_lon": doc.gps_lon,
   "gps_acc": doc.gps_acc,
   "wifi_lat": doc.wifi_lat,
   "wifi_lon": doc.wifi_lon,
   "usr_lat": doc.usr_lat,
   "usr_lon": doc.usr_lon,
   "timestamp": doc.timestamp});
  }
}
